bytime.map:
function(doc) {
  if (doc.oda_type && doc.oda_type == 'lectures') {
    for (var i in doc.showtimes) {
      emit(doc.showtimes[i].start, doc.title);
    }
  }
}

showtimes.map:
function(doc) {
  if (doc.oda_type && doc.oda_type == 'lectures') {
    for (var i in doc.showtimes) {
      emit(doc.title, doc.showtimes[i]);
    }
  }
}

tags.map:
function(doc) {
  if (doc.oda_type && doc.oda_type == 'lectures') {
    emit(doc.lecture_id, [doc.name.en, doc.geo.location, doc.oda_tags]);
  }
}

countbytopic.map:
function(doc) {
  if (doc.oda_type) {
    if (doc.oda_type == 'points') {
      emit(doc.subcategory.name, doc.name);
    } else if (doc.oda_type == 'lectures') {
      doc.topics.forEach(function(topic) {
        emit(topic, doc.title);
      });
    }
  }
}

countbylang.map:
function(doc) {
  if (doc.oda_type) {
    if (doc.oda_type == 'points') {
      if (doc.languages.en) {
      	emit("en", doc.name);
      }
      if (doc.languages.fr) {
      	emit("fr", doc.name);
      }
      if (doc.languages.de) {
      	emit("de", doc.name);
      }
      if (doc.languages.it) {
      	emit("it", doc.name);
      }
      if (doc.languages.es) {
      	emit("es", doc.name);
      }
    } else if (doc.oda_type == 'lectures') {
      emit(language, doc.title);
    }
  }
}

images.map:
function(doc) {
  if (doc.oda_type) {
    if (doc._attachments && "img_large" in doc._attachments) {
      if (doc.oda_type == 'points') {
        emit("img_large", doc.name);
      } else if (doc.oda_type == 'points' || doc.oda_type == 'static') {
	emit("img_large", doc.title);
      }
    }
  }
}

ludmila.map:
function(doc) {
  if (doc.oda_type=='points' && (doc.category.id == 1 || doc.category.id == 2 || doc.category.id == 3)) {
  	emit(doc.name.en, {'name':doc.name, 'site':doc.geo.site, 'code':doc.code, 'description': doc.description});
  }
}

all_points_status.map:
function(doc) {
  if (doc.oda_type) {
    if (doc.oda_type == 'points') {
      emit([doc._id, 0], doc);
    } else if (doc.oda_type == 'point_status') {
      var id = "point_" + doc.point_id
      emit([id, 1], doc);
    }
  }
}
